
public class ForLoopAssignHelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Goal is to print "hello world" five times
		String hw = "Hello World"; // String that holds the message
		
		for(int i = 0;i<=4;i++) {
			System.out.println(hw);
		}
		
	}

}
