
public class NameArrayWithWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		// Name (individual names) array with while loop
        
        String[] name = new String[4];
        
        name[0] = "Harry";
        name[1] = "Raj";
        name[2] = "Kumar";
        name[3] = "John";
        
        int i = 0;
        
        while(i<4) {
        	System.out.println(name[i]);
        	i++;
        }
		        
	}

}
